import React from "react";
import "./assets/ex_libs/bootstrap_4/bootstrap.css";
import "./assets/css/style.css";

const [popupVisible, setPopupVisible] = useState(false);
    const [newData, setNewData] = useState("");
    const [targetElementId, setTargetElementId] = useState("");

    const showPopup = (elementId, type) => {
        setTargetElementId(elementId);
        setPopupVisible(true);
    };

    const hidePopup = () => {
        setPopupVisible(false);
        setNewData(""); // Clear the input field
    };

    const changeData = () => {
        const targetElement = document.getElementById(targetElementId);
        if (targetElement) {
            targetElement.textContent = newData;
        }
        hidePopup();
    };

function Profile() {

    return (
        <>
        <div>
            <script src="./assets/ex_libs/jQuery/jquery-3.6.4.slim.min.js"></script>
            <script src="./assets/ex_libs/bootstrap_4/bootstrap.js"></script>
            <noscript>You need to enable JavaScript to run this app.</noscript>
            <nav className="navbar navbar-expand-sm bg-dark navbar-dark fixed-top">
                <a className="navbar-brand" href="#/">
                    <i class="fas fa-angle-left"></i>

                </a>
                <a
                    className="navbar-brand navbar-nav-text-heading"
                    href="#/"
                >
                    Ark Connections
                </a>
                <button
                    className="navbar-toggler"
                    type="button"
                    data-toggle="collapse"
                    data-target="#collapsibleNavbar"
                >
                    <span className="navbar-toggler-icon"></span>
                </button>
                <div
                    className="collapse navbar-collapse"
                    id="collapsibleNavbar"
                >
                    <ul className="navbar-nav">
                        <li className="nav-item">
                            <a className="nav-link" href="#/">
                                Home
                            </a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="#/quiz">
                                Quiz
                            </a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link" href="#/results">
                                Results
                            </a>
                        </li>
                        <li className="nav-item">
                            <a className="nav-link active" href="#/profile">
                                Profile
                            </a>
                        </li>
                    </ul>
                </div>
            </nav>
        </div>

        <i className="fas fa-user-alt" id="user-profile"></i>
        <p id="user-id">Alistair Tan <span className="fas fa-pen" id="edit-name" onClick={() => showPopup('user-id', 'Username')}></span></p>

        {popupVisible && (
        <div id="popup">
            <label htmlFor="new-data">New Data:</label>
            <input
                type="text"
                id="new-data"
                value={newData}
                onChange={(e) => setNewData(e.target.value)}/>
            <button onClick={changeData}><i className="far fa-save"></i></button>
            <button onClick={hidePopup}>Cancel</button>
        </div>)}
        </>
    );
}

export default Profile;
